mapper.select:

where 1=1

    and i.`softwareName` LIKE CONCAT('%',#{softwareName},'%')


    and i.status = #{status}


    and i.flatformId = #{flatformId}


    and i.categoryLevel1 = #{categoryLevel1}


    and i.categoryLevel2 = #{categoryLevel2}


    and i.categoryLevel3 = #{categoryLevel3}

limit #{index},#{pageSize}

select count(1)
FROM `app_info` i
INNER JOIN `data_dictionary` d ON (d.valueId=i.`flatformId` AND d.`typeCode`='APP_FLATFORM')
INNER JOIN `app_category` c ON (c.`id`=i.`categoryLevel1`)
INNER JOIN `app_category` a ON (a.`id`=i.`categoryLevel2`)
INNER JOIN `app_category` t ON (t.`id`=i.`categoryLevel3`)
INNER JOIN `data_dictionary` o ON (o.`typeCode`='APP_STATUS' AND o.`valueId`=i.`status`)
LEFT JOIN `app_version` v ON (v.`id`=i.versionId)
where 1=1

    and i.`softwareName` LIKE CONCAT('%',#{softwareName},'%')


    and i.status = #{status}


    and i.flatformId = #{flatformId}


    and i.categoryLevel1 = #{categoryLevel1}


    and i.categoryLevel2 = #{categoryLevel2}


    and i.categoryLevel3 = #{categoryLevel3}
